import React, { useEffect } from 'react';
import { checkVideoExists } from '../controller/videoPlaybackController';
import '../css/videoPlayback.css';

// type de l'id de la video à visionner
interface VideoPlaybackViewProps {
  videoId: string;
}

const VideoPlaybackView: React.FC<VideoPlaybackViewProps> = ({ videoId }) => {
  
  // appelé à chaque fois que le composant est appelé et à chaque fois que videoId change
  useEffect(() => {
    const fetchVideo = async () => {
      const videoExists = await checkVideoExists(videoId);
      if (videoExists) {
        const videoIframe = document.getElementById('videoIframe') as HTMLIFrameElement;
        videoIframe.src = `https://www.youtube.com/embed/${videoId}`;
      } else {
        alert('Vidéo non trouvée');
      }
    };

    fetchVideo();
  }, [videoId]);

  return (
    <div>
      <h1 className="video-title">Titre de la vidéo</h1>
      <iframe
        id="videoIframe"
        frameBorder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
        title="YouTube video"
      ></iframe>
    </div>
  );
};

export default VideoPlaybackView;
