// LibraryView.tsx
import React from 'react';
import MyVideoView from './MyVideoView';
import { getUserVideos } from '../controllers/libraryController';
import { useFormState } from '../hooks/useFormState';
import '../css/style.css';

interface LibraryViewProps {
  username: string;
}

const LibraryView: React.FC<LibraryViewProps> = ({ username }) => {
  const userVideos = getUserVideos({ username });
  const { isFormVisible, toggleFormVisibility } = useFormState();

  return (
    <div className="Library">
      <h1>Library {username}</h1>
      <button className="DisplayForm" onClick={toggleFormVisibility}>
        {isFormVisible ? '[-]' : '[+]'}
      </button>
      {isFormVisible && <div>Form Content Here</div>}
      <div className="library-view-container">
        {userVideos.map((video, index) => (
          <div className="library-view-item" key={index}>
            <MyVideoView title={video.title} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default LibraryView;
