interface Video {
  title: string;
  id: string;
}

interface UserLibrary {
  name: string;
  videos: Video[];
}

class LibraryModel {
  private static apiBaseUrl = 'http://localhost/youtube_library/api'; // Remplacez par le chemin correct

  public static async getLibrary(userName: string): Promise<UserLibrary> {
    const response = await fetch(`${this.apiBaseUrl}/get_library.php?userName=${userName}`);
    if (!response.ok) {
      const errorMessage = await response.text();
      console.error(`Error fetching library: ${errorMessage}`);
      throw new Error(`Library not found: ${errorMessage}`);
    }
    try {
      const data = await response.json();
      console.log('Fetched Library:', data); // Log the fetched library
      return data as UserLibrary;
    } catch (error) {
      console.error('Failed to parse JSON response:', error);
      throw new Error('Invalid JSON response');
    }
  }

  public static async saveLibrary(userName: string, library: UserLibrary): Promise<void> {
    const response = await fetch(`${this.apiBaseUrl}/save_library.php?userName=${userName}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(library)
    });
    if (!response.ok) {
      const errorMessage = await response.text();
      console.error(`Error saving library: ${errorMessage}`);
      throw new Error(`Failed to save library: ${errorMessage}`);
    }
    console.log('Library saved successfully:', library); // Log the saved library
  }

  public static async addVideo(userName: string, video: Video): Promise<void> {
    try {
      const library = await LibraryModel.getLibrary(userName);
      library.videos.push(video);
      await LibraryModel.saveLibrary(userName, library);
      console.log(`Video with ID: ${video.id} added to the library of user: ${library.name}`);
    } catch (error: unknown) {
      if (error instanceof Error) {
        console.error(`Error adding video: ${error.message}`);
      } else {
        console.error('Unexpected error', error);
      }
    }
  }

  public static async removeVideo(userName: string, videoId: string): Promise<void> {
    try {
      const library = await LibraryModel.getLibrary(userName);
      console.log('Library before removal:', library); // Log the library before removal
      console.log('Video IDs in library:', library.videos.map(video => video.id)); // Log all video IDs
      const videoIndex = library.videos.findIndex(video => video.id === videoId);
      console.log('Found video index:', videoIndex); // Log the index found

      if (videoIndex !== -1) {
        library.videos.splice(videoIndex, 1);
        await LibraryModel.saveLibrary(userName, library);
        console.log(`Video with ID: ${videoId} removed from the library of user: ${library.name}`);
      } else {
        console.log(`Video with ID: ${videoId} not found in the library of user: ${library.name}`);
      }
    } catch (error: unknown) {
      if (error instanceof Error) {
        console.error(`Error removing video: ${error.message}`);
      } else {
        console.error('Unexpected error', error);
      }
    }
  }
}

export default LibraryModel;
