import React, { Component } from 'react';
import './css/style.css';
import LibraryView from './view/LibraryView';
import { addBook, getBooks, deleteBook } from './controller/libraryController';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: []
    };
  }

  componentDidMount() {
    this.setState({ books: getBooks() });
  }

  handleAddBook = (title, author) => {
    const result = addBook(title, author);
    if (result.success) {
      this.setState({ books: getBooks() });
    } else {
      alert(result.message);
    }
  };

  handleDeleteBook = (index) => {
    deleteBook(index);
    this.setState({ books: getBooks() });
  };

  render() {
    return (
      <div className="app-container">
        <LibraryView
          books={this.state.books}
          onAddBook={this.handleAddBook}
          onDeleteBook={this.handleDeleteBook}
        />
      </div>
    );
  }
}

export default App;
