import React from 'react';
import MyVideoView from './MyVideoView';
import '../css/library.css';

// types des éléments du tableaux vidéos de la base de données
interface video {
  title: string;
  id: string;
}

// type du nom et des vidéos de l'utilisateur
interface LibraryViewProps {
  username: string;
  userVideos: video[];
  displayFormOnClick: (event: React.FormEvent<HTMLButtonElement>) => void;
  libraryItemOnClick: (event: React.FormEvent<HTMLButtonElement>, video: video) => void;
}

// Composant qui affiche l'élément de gauche de la vue
class LibraryView extends React.Component<LibraryViewProps> {
  render() {
    const { username, userVideos, displayFormOnClick, libraryItemOnClick } = this.props;
    return (
      <div className="library-container">
        <div className="library-header">
          <h1>Library {username}</h1>
        </div>
        <button className="display-form" onClick={displayFormOnClick}>[+]</button>
        <div className="library-items">
          {/* Affiche un bouton pour chaque vidéo de l'utilisateur */}
          {userVideos.map((video) => (
            <div key={video.id} className="library-item"> {/* Ajout de la clé unique */}
              {/* Affiche bouton pour un titre de vidéo donné */}
              <MyVideoView onClick={libraryItemOnClick} video={video} /> {/* Correction des props */}
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default LibraryView;
