import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import './css/style.css';

const getUsernameFromURL = (): string | null => {
  const params = new URLSearchParams(window.location.search);
  return params.get('user');
}

const Main: React.FC = () => {
  const [username, setUsername] = useState<string | null>(null);

  useEffect(() => {
    // Récupérer le nom d'utilisateur depuis l'URL
    const user = getUsernameFromURL();
    setUsername(user);
  }, []);

  return (
    <div>
      {/* Afficher le nom d'utilisateur pour déboguer */}
      <div>Nom d'utilisateur: {username}</div>
      
      {/* Charger l'application avec le nom d'utilisateur */}
      {username && <App username={username} />}
    </div>
  );
}

ReactDOM.render(
  <Main />,
  document.getElementById('root')
);
