import React from 'react';

interface Video {
  title: string;
  id: string;
}

interface SearchResultsViewProps {
  results: any[];
  onVideoAdd: (video: Video) => void; // Ajout de la fonction pour ajouter une vid√©o
}

class SearchResultsView extends React.Component<SearchResultsViewProps> {
  render() {
    const { results, onVideoAdd } = this.props;
    return (
      <div className="results-container">
        {results.map((result, index) => (
          <div key={result.id.videoId} className="result-item">
            <img src={result.snippet.thumbnails.default.url} alt={result.snippet.title} />
            <h3>{`${index + 1}: ${result.snippet.title}`}</h3>
            <button onClick={() => onVideoAdd({ title: result.snippet.title, id: result.id.videoId })}>Add</button>
          </div>
        ))}
      </div>
    );
  }
}

export default SearchResultsView;
