import React from 'react';
import '../css/mainPanel.css';

interface Video {
  title: string;
  id: string;
}

interface SearchResultsViewProps {
  results: any[];
  onAddVideo: (video: Video) => void;
}

interface SearchResultsViewState {
  titles: { [key: string]: string };
}

// composant qui affiche les résultats d'une recherche
class SearchResultsView extends React.Component<SearchResultsViewProps, SearchResultsViewState> {
  constructor(props: SearchResultsViewProps) {
    super(props);
    this.state = { titles: {} };

    this.handleTitleChange = this.handleTitleChange.bind(this);
    this.handleAddVideo = this.handleAddVideo.bind(this);
  }

  handleTitleChange(event: React.ChangeEvent<HTMLInputElement>, videoId: string) {
    this.setState({
      titles: {
        ...this.state.titles,
        [videoId]: event.target.value
      }
    });
  }

  handleAddVideo(videoId: string, defaultTitle: string) {
    const title = this.state.titles[videoId] || defaultTitle;
    this.props.onAddVideo({ title, id: videoId });
  }

  render() {
    const { results } = this.props;
    return (
      <div className="results-container">
        {results.map((result) => (
          <div key={result.id.videoId} className="result-item">
            <img src={result.snippet.thumbnails.default.url} alt={result.snippet.title} />
            <h3>{`${result.snippet.title}`}</h3>
            <input
              type="text"
              placeholder="Enter custom title"
              value={this.state.titles[result.id.videoId] || ''}
              onChange={(event) => this.handleTitleChange(event, result.id.videoId)}
            />
            <button onClick={() => this.handleAddVideo(result.id.videoId, result.snippet.title)}>+</button>
          </div>
        ))}
      </div>
    );
  }
}

export default SearchResultsView;
