import johnVideosData from '../lib/John.json';
import markVideosData from '../lib/Mark.json';

interface Video {
  title: string;
  id: string;
}

interface LibraryModelProps {
  username: string;
}

class LibraryModel {
  private johnVideos: Video[];
  private markVideos: Video[];

  constructor() {
    this.johnVideos = johnVideosData.videos;
    this.markVideos = markVideosData.videos;
  }

  getVideosByUsername(username: string): Video[] {
    if (username === "john") {
      return this.johnVideos;
    } 

    if (username === "mark") {
      return this.markVideos;
    }

    // Si le username ne correspond ni à "john" ni à "mark", on retourne un tableau vide
    return [];
  }
}

export default LibraryModel;
