import React from 'react';
import MyVideo from './MyVideo';
import Model from '../model/libraryModel';

interface LibraryViewProps {
  username: string;
}

class LibraryView extends React.Component<LibraryViewProps> {
  render() {
    const { username } = this.props;
    // Appeler la fonction libraryModel pour obtenir les vidéos de l'utilisateur
    const userVideos = Model.libraryModel({ username });

    return (
      <div className="Library">
        <h1>Library {username}</h1>
        <button className="DisplayForm">[+]</button>
        
        {/* Afficher les titres des vidéos */}
        <ul>
          {userVideos.map((video, index) => (
            <li key={index}>{video.title}</li>
          ))}
        </ul>
      </div>
    );
  }
}

export default LibraryView;
