import React from 'react';
import '../css/searchBar.css';

// Type pour le nom de l'utilisateur et les vidéos
interface SearchBarViewProps {
    value: string;
    onChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
    onSubmit: (event: React.FormEvent<HTMLFormElement>) => void;
    numResults: number;
    onNumResultsChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
}

// Composant qui affiche la barre de recherche et le champ pour le nombre de résultats
class SearchBarView extends React.Component<SearchBarViewProps> {
    handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        this.props.onSubmit(event);
    };

    render() {
        return (
            <form className="search-bar-form" onSubmit={this.handleSubmit}>
                {/* Barre de recherche */}
                <input
                    type="text"
                    placeholder="Rechercher une vidéo"
                    value={this.props.value}
                    onChange={this.props.onChange}
                />

                {/* Champ pour le nombre de résultats */}
                <input
                    type="number"
                    placeholder="Nombre de résultats"
                    value={this.props.numResults.toString()}
                    onChange={this.props.onNumResultsChange}
                    min="1"
                    max="5"
                />
            </form>
        );
    }
}

export default SearchBarView;
