import { addBookToModel, getBooksFromModel, deleteBookFromModel } from '../model/libraryModel';

export const addBook = (title, author) => {
  if (title.trim() === '' || author.trim() === '') {
    return { success: false, message: 'Title and author cannot be empty' };
  }
  addBookToModel(title, author);
  return { success: true, message: 'Book added successfully' };
};

export const getBooks = () => {
  return getBooksFromModel();
};

export const deleteBook = (index) => {
  deleteBookFromModel(index);
  return { success: true, message: 'Book deleted successfully' };
};
